about.usage:
// Wrap in ThemeProvider
import { TrendingUp, ShoppingCart, Users, Globe } from 'lucide-react';

<ThemeProvider defaultButtonVariant="icon-arrow" defaultTextAnimation="reveal-blur" borderRadius="soft" background="aurora" cardStyle="glass-elevated" primaryButtonStyle="gradient" secondaryButtonStyle="glass">
  <AboutMetric 
    title="At Berco Inc. we redefine innovation by empowering businesses to reach their full potential" 
    metrics={[
      { icon: TrendingUp, label: 'Conversions', value: '7,000+' },
      { icon: ShoppingCart, label: 'Ecommerce Since', value: '2018' },
      { icon: Users, label: 'Partnered Brands', value: '30+' },
      { icon: Globe, label: 'Global Impressions Generated Annually', value: '100M+' }
    ]} 
  />
</ThemeProvider>

// Wrap in ThemeProvider
import { Rocket, Users, Target, Sparkles } from 'lucide-react';

<ThemeProvider defaultButtonVariant="text-stagger" defaultTextAnimation="entrance-slide" borderRadius="rounded" contentWidth="medium" sizing="medium" background="aurora" cardStyle="glass-elevated" primaryButtonStyle="gradient" secondaryButtonStyle="glass">
  <AboutFeature 
    title="We believe in creating experiences that inspire, empower, and transform. Our team is dedicated to building solutions that make a real difference." 
    features={[
      { icon: Rocket, title: 'Innovation First', description: 'We push boundaries and embrace cutting-edge technologies to deliver exceptional results that exceed expectations.' },
      { icon: Users, title: 'Team Collaboration', description: 'Our diverse team works together seamlessly to bring your vision to life with expertise and dedication.' },
      { icon: Target, title: 'Goal Oriented', description: 'We focus on measurable outcomes and strategic objectives to ensure your success and growth.' },
      { icon: Sparkles, title: 'Quality Driven', description: 'Every detail matters. We maintain the highest standards in everything we create and deliver.' }
    ]} 
  />
</ThemeProvider>

// Wrap in ThemeProvider

<ThemeProvider defaultButtonVariant="text-stagger" defaultTextAnimation="entrance-slide" borderRadius="rounded" contentWidth="medium" sizing="medium" background="aurora" cardStyle="glass-elevated" primaryButtonStyle="gradient" secondaryButtonStyle="glass">
  <SplitAboutMetric 
    title="A Legacy Of Timeless Elegance" 
    description={[
      "Shivalik developers are on a quest to build a legacy of timeless elegance. Pioneering the skyline of the city already, they are steadily conquering the state of Gujarat on the front of luxurious Residential & Commercial Spaces."
    ]} 
    metrics={[
      { label: 'Various types of landmarks built', value: '75+' },
      { label: 'Sq.ft area', value: '15M+' }
    ]} 
  />
</ThemeProvider>

// Wrap in ThemeProvider with smooth scrolling (ReactLenis)
<ThemeProvider defaultButtonVariant="icon-arrow" defaultTextAnimation="entrance-slide" borderRadius="pill" contentWidth="medium" sizing="medium" background="aurora" cardStyle="layered-gradient" primaryButtonStyle="gradient" secondaryButtonStyle="glass">
  <ReactLenis root>
    <AboutPhoneTimeline 
      tag="About Us" 
      title="Our Mission" 
      description="We believe in creating experiences that inspire, empower, and transform. Our team is dedicated to building solutions that make a real difference in people's lives." 
      phoneOne={{ imageSrc: '/app-screenshot-1.jpg', imageAlt: 'Dashboard view' }} 
      phoneTwo={{ videoSrc: '/app-demo.mp4', videoAriaLabel: 'Product demo video' }} 
      buttons={[{ text: 'Learn More', href: '/about' }, { text: 'Get Started', href: '/signup' }]} 
    />
  </ReactLenis>
</ThemeProvider>
